              target="_blank" color="primary">
              Comprar 200W
            </v-btn>
          </v-card-actions>
        </v-card>
      </v-col>
    </v-row>

  </v-container>
</template>

<script>
export default {
  name: 'AquariumPlanner',
  data() {
    return {
      aquariumType: 'freshwater',
      aquariumSize: 100,
      aquariumLength: 80,
      aquariumWidth: 35,
      aquariumHeight: 40,
      calculatedVolume: 0,
      hasPlants: true,
      hasHeater: true,
      capacityCalculated: false,
      maxSmallFish: 0,
      maxMediumFish: 0,
      maxLargeFish: 0,
      recommendedFish: [],
      availableFish: [
        { name: 'Neon Tetra', size: 'Pequeno', typeNeeded: 'freshwater' },
        { name: 'Guppy', size: 'Pequeno', typeNeeded: 'freshwater' },
        { name: 'Betta', size: 'Pequeno', typeNeeded: 'freshwater' },
        { name: 'Corydoras', size: 'Pequeno', typeNeeded: 'freshwater' },
        { name: 'Oscar', size: 'Grande', typeNeeded: 'freshwater' },
        { name: 'Discus', size: 'Médio', typeNeeded: 'freshwater' },
        { name: 'Tanganyika Cichlid', size: 'Médio', typeNeeded: 'freshwater' },
        { name: 'Clownfish', size: 'Pequeno', typeNeeded: 'saltwater' },
        { name: 'Blue Tang', size: 'Médio', typeNeeded: 'saltwater' },
        { name: 'Lionfish', size: 'Grande', typeNeeded: 'saltwater' },
        { name: 'Coral Beauty', size: 'Médio', typeNeeded: 'saltwater' },
        { name: 'Mandarin Dragonet', size: 'Pequeno', typeNeeded: 'saltwater' },
      ],
      plants: [
        { id: 1, name: 'Anubia', image: 'data:image/jpeg;base64,' },
        { id: 2, name: 'Musgo de Java', image: 'data:image/jpeg;base64,' },
        { id: 3, name: 'Amazonense', image: 'data:image/jpeg;base64,' }
      ],
      heaters: [
        { id: 1, name: 'Aquecedor 50W', power: 50, image: 'https://http2.mlstatic.com/D_NQ_NP_619397-MLU73883509561_012024-O.webp' },
        { id: 2, name: 'Aquecedor 100W', power: 100, image: 'https://http2.mlstatic.com/D_NQ_NP_942296-MLB80277322707_102024-O-termostato-ocean-tech-100w-aquecedor-x-6-aquarios-ate-100l.webp' },
        { id: 3, name: 'Aquecedor 200W', power: 200, image: 'https://http2.mlstatic.com/D_NQ_NP_954245-MLU71759031275_092023-O.webp' }
      ]
    }
  },
  mounted() {
    this.calculateVolume();
  },
  methods: {
    calculateVolume() {
      this.calculatedVolume = Math.round((this.aquariumLength * this.aquariumWidth * this.aquariumHeight) / 1000);
      if (this.calculatedVolume > 0) this.aquariumSize = this.calculatedVolume;
    },
    calculateCapacity() {
      // Regra simples: 1 litro para cada cm de peixe pequeno
      this.maxSmallFish = Math.floor(this.aquariumSize / 2);
      this.maxMediumFish = Math.floor(this.aquariumSize / 5);
      this.maxLargeFish = Math.floor(this.aquariumSize / 10);

      // Filtrando peixes que correspondem ao tipo de aquário (água doce ou salgada)
      this.recommendedFish = this.availableFish.filter(fish => fish.typeNeeded === this.aquariumType);

      // Selecionando no máximo 3 sugestões aleatórias
      this.recommendedFish = this.recommendedFish.sort(() => 0.5 - Math.random()).slice(0, 3);

      this.capacityCalculated = true;
    }

  }
}
</script>
